function findSecondLargestNumber(arr) {
  let max = Math.max(...arr);
  let secondMax = -Infinity;

  for (let i = 0; i < arr.length; i++) {
    if (arr[i] < max && arr[i] > secondMax) {
      secondMax = arr[i];
    }
  }

  return secondMax;
}

